mapper.insert:
INSERT INTO
craneinspectreport(

)
VALUES (#{id}, #{reportNumber}, #{unitAddress},#{addressId},#{organizeCode},#{userPoint},#{safeManager},#{contactPhone},#{equipmentVariety},#{unitNumber},#{manufactureUnit},#{manufactureLicenseNumber},#{manufactureDate},#{specification},#{pNumber},#{workLevel},#{lng},#{lat});

    select
    LAST_INSERT_ID() as id

mapper.update:
UPDATE craneinspectreport
UNITADDRESS=#{unitAddress}, ADDRESSID=#{addressId}, ORGANIZECODE=#{organizeCode}, USEPOINT=#{userPoint}, SAFEMANAGER=#{safeManager}, CONTACTPHONE=#{contactPhone}, EQUIPMENTVARIETY=#{equipmentVariety}, UNITNUMBER=#{unitNumber}, MANUFACTUREUNIT=#{manufactureUnit}, MANUFACTURELICENSENUMBER=#{manufactureLicenseNumber}, MANUFACTUREDATE=#{manufactureDate}, SPECIFICATION=#{specification}, PNUMBER=#{pNumber}, WORKLEVEL=#{workLevel}
WHERE REPORTNUMBER=#{reportNumber};

mapper.select:
SELECT reportNumber
FROM craneinspectreport
WHERE REPORTNUMBER=#{reportNumber}

SELECT

FROM craneinspectreport

    REPORTNUMBER=#{reportNumber}
    AND UNITADDRESS=#{unitAddress}
    AND ADDRESSID=#{addressId}
    AND ORGANIZECODE=#{organizeCode}
    AND USEPOINT=#{userPoint}
    AND SAFEMANAGER=#{safeManager}
    AND CONTACTPHONE=#{contactPhone}
    AND EQUIPMENTVARIETY=#{equipmentVariety}
    AND UNITNUMBER=#{unitNumber}
    AND MANUFACTUREUNIT=#{manufactureUnit}
    AND MANUFACTURELICENSENUMBER=#{manufactureLicenseNumber}
    AND MANUFACTUREDATE=#{manufactureDate}
    AND SPECIFICATION=#{specification}
    AND PNUMBER=#{pNumber}
    AND WORKLEVEL=#{workLevel}

select
distinct (unitaddress)
from craneinspectreport
where
addressid=(
SELECT id
FROM address


        PROVINCE=#{province}


        and CITY=#{city}


        and AREA=#{area}


)
