mapper.select:
SELECT s.id,s.`name`,s.description,s.number,g.`name` AS groupName,a.`name` AS areaName,c.`name` AS collectorName,s.maxFrequency,s.minFrequency,s.workFrequency,s.shouldWarn,s.warnType,s.warnValue,s.warnCount,s.warnStatus,s.appId
FROM `group` g,area a,collector c,sensor s
WHERE s.groupId=g.id AND s.areaId=a.id AND s.collectorId=c.id AND s.appId=#{appId}

mapper.update:
UPDATE sensor
NAME=#{name}, DESCRIPTION=#{description}, NUMBER=#{number}, GROUPID=#{groupId}, AREAID=#{areaId}, COLLECTORID=#{collectorId}, MAXFREQUENCY=#{maxFrequency}, MINFREQUENCY=#{minFrequency}, WORKFREQUENCY=#{workFrequency}, SHOULDWARN=#{shouldWarn}, WARNTYPE=#{warnType}, WARNVALUE=#{warnValue}
WHERE ID=#{id} AND APPID=#{appId}

mapper.select:
SELECT s.id,s.name,g.`name` AS groupName,a.`name` AS areaName,s.warnType,s.warnValue,s.warnCount,c.status AS status,c.`name` AS collectorName,s.number
FROM `group` g,area a,collector c,sensor s
WHERE s.groupId=g.id AND s.areaId=a.id AND s.collectorId=c.id AND s.appId=#{appId}

mapper.update:
UPDATE sensor

    WARNCOUNT=#{warnCount}

WHERE id=#{id} AND appId=#{appId}
