mapper.insert:
INSERT INTO
device(

)
VALUES (#{id}, #{mainDeviceId},#{batchId},#{supplierId},#{typeId},#{storehouseId},#{contractId},#{address},#{status},#{name},#{number},#{price},#{produceTime},#{createTime},#{priceUnit},#{lng},#{lat},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.update:
update device
BATCHID=#{batchId}, SUPPLIERID=#{supplierId}, TYPEID=#{typeId}, STOREHOUSEID=#{storehouseId}, CONTRACTID=#{contractId}, ADDRESS=#{address}, STATUS=#{status}, NAME=#{name}, NUMBER=#{number}, PRICE=#{price}, PRODUCETIME=#{produceTime}, PRICEUNIT=#{priceUnit}, LNG=#{lng}, LAT=#{lat}, APPID=#{appId}
where ID=#{id};

mapper.select:
select id
from device
where NUMBER=#{number} and APPID=#{appId}

select device.id,device.name as name,device.number,device_type.name as deviceType,storehouse.name as storehouse,contract.name as contract,device.price,device.createTime,supplier.name as supplierName,
device.produceTime,device.price,device.status,batch.number as batchNumber
from device,device_type,storehouse,contract,batch,supplier
where device.typeId=device_type.id and device.storehouseId=storehouse.id and device.contractId=contract.id and device.batchId=batch.id and device.supplierId=supplier.id
and device.appId=#{appId}
 AND device.APPID=#{appId}
 AND device.optionType=#{optionType}
 AND device.storehouseId=#{storehouseId}
 AND device_type.mainDevice=#{mainDevice}
AND device.number like CONCAT('%',#{number},'%') 
AND device.name like CONCAT('%',#{name},'%')
 AND device.produceTime>=#{startTime}
 AND device.produceTime<=#{endTime}
