mapper.insert:
INSERT INTO
craneinspectreport(

)
VALUES (#{id}, #{reportNumber}, #{unitAddress},#{addressId},#{organizeCode},#{userPoint},#{safeManager},#{contactPhone},#{equipmentVariety},#{unitNumber},#{manufactureUnit},#{manufactureLicenseNumber},#{manufactureDate},#{specification},#{pNumber},#{workLevel},#{lng},#{lat},#{singlePicURL},#{typePicURL},#{ratedLiftWeight});

    select
    LAST_INSERT_ID() as id

mapper.update:
UPDATE craneinspectreport
UNITADDRESS=#{unitAddress}, ADDRESSID=#{addressId}, ORGANIZECODE=#{organizeCode}, USEPOINT=#{userPoint}, SAFEMANAGER=#{safeManager}, CONTACTPHONE=#{contactPhone}, EQUIPMENTVARIETY=#{equipmentVariety}, UNITNUMBER=#{unitNumber}, MANUFACTUREUNIT=#{manufactureUnit}, MANUFACTURELICENSENUMBER=#{manufactureLicenseNumber}, MANUFACTUREDATE=#{manufactureDate}, SPECIFICATION=#{specification}, PNUMBER=#{pNumber}, WORKLEVEL=#{workLevel}, SINGLEPICURL=#{singlePicURL}, TYPEPICURL=#{typePicURL}, RATEDLIFTWEIGHT=#{ratedLiftWeight}
WHERE REPORTNUMBER=#{reportNumber};

mapper.select:
SELECT reportNumber
FROM craneinspectreport
WHERE REPORTNUMBER=#{reportNumber}

SELECT

FROM craneinspectreport

    REPORTNUMBER=#{reportNumber}
    AND UNITADDRESS=#{unitAddress}
    AND ADDRESSID=#{addressId}
    AND ORGANIZECODE=#{organizeCode}
    AND USEPOINT=#{userPoint}
    AND SAFEMANAGER=#{safeManager}
    AND CONTACTPHONE=#{contactPhone}
    AND EQUIPMENTVARIETY=#{equipmentVariety}
    AND UNITNUMBER=#{unitNumber}
    AND MANUFACTUREUNIT=#{manufactureUnit}
    AND MANUFACTURELICENSENUMBER=#{manufactureLicenseNumber}
    AND MANUFACTUREDATE=#{manufactureDate}
    AND SPECIFICATION=#{specification}
    AND PNUMBER=#{pNumber}
    AND WORKLEVEL=#{workLevel}
    AND SINGLEPICURL=#{singlePicURL} 
    AND TYPEPICURL=#{typePicURL}
    AND RATEDLIFTWEIGHT=#{ratedLiftWeight}

SELECT

FROM  craneinspectreport
where addressId=#{addressId}

SELECT

FROM craneinspectreport
WHERE reportNumber=#{reportNumber}

select
distinct (unitaddress),lng,lat
from craneinspectreport
where
addressid=(
SELECT id
FROM address


        PROVINCE=#{province}


        and CITY=#{city}


        and AREA=#{area}


)

SELECT cr.*,r.riskvalue
FROM craneinspectreport cr,riskvalue r
WHERE
cr.reportnumber=r.reportnumber
AND
lng
BETWEEN #{minLng}
AND #{maxLng}
AND lat
BETWEEN #{minLat}
AND #{maxLat}
GROUP BY unitAddress

SELECT cr.id,cr.unitAddress,r.riskvalue,cr.equipmentvariety,cr.lng,cr.lat
FROM craneinspectreport cr,riskvalue r,address a
WHERE cr.reportnumber=r.reportnumber
AND a.id=cr.addressId  AND a.city=#{city} AND a.area=#{area}
AND cr.unitaddress like  #{require}
order by r.riskvalue desc

SELECT cr.id,cr.unitAddress,r.riskvalue,cr.equipmentvariety,cr.lng,cr.lat
FROM craneinspectreport cr,riskvalue r,address a
WHERE cr.reportnumber=r.reportnumber
AND a.id=cr.addressId  AND a.city=#{city} AND a.area=#{area}
AND cr.usePoint like  #{require}
order by r.riskvalue desc

SELECT cr.id,cr.unitAddress,r.riskvalue,cr.equipmentvariety,cr.lng,cr.lat
FROM craneinspectreport cr,riskvalue r,address a
WHERE cr.reportnumber=r.reportnumber
AND a.id=cr.addressId  AND a.city=#{city} AND a.area=#{area}
AND cr.safeManager like  #{require}
order by r.riskvalue desc

SELECT cr.id,cr.unitAddress,r.riskvalue,cr.equipmentvariety,cr.lng,cr.lat
FROM craneinspectreport cr,riskvalue r,address a
WHERE cr.reportnumber=r.reportnumber
AND a.id=cr.addressId  AND a.city=#{city} AND a.area=#{area}
AND cr.equipmentVariety like  #{require}
order by r.riskvalue desc

SELECT cr.id,cr.unitAddress,r.riskvalue,cr.equipmentvariety,cr.lng,cr.lat
FROM craneinspectreport cr,riskvalue r,address a
WHERE cr.reportnumber=r.reportnumber
AND a.id=cr.addressId  AND a.city=#{city} AND a.area=#{area}
AND cr.manufactureUnit like  #{require}
order by r.riskvalue desc
